mapper.select:
select count(*)
from wechatdata
 where YEAR(latestDate)=#{year}
      AND MONTH(latestDate)=#{month}
      AND DAY(latestDate)=#{day}

select
    headPicture,wxid,wxName,wxBiz,articlesNum,totalOriginalNum,totalReadNum,
    totalLikeNum,avgReadNum,avgLikeNum,avgHeadlineNum,qualityNum,influenceNum,latestDate,di,ri,li
from wechatdata
where YEAR(latestDate)=#{year}
      AND MONTH(latestDate)=#{month}
      AND DAY(latestDate)=#{day}
      ORDER BY
              ${order} DESC
limit
      #{start},#{pageSize}

select
      headPicture,wxid,wxName,wxBiz,articlesNum,totalOriginalNum,totalReadNum,
      totalLikeNum,avgReadNum,avgLikeNum,avgHeadlineNum,qualityNum,influenceNum,latestDate,di,ri,li
from
      wechatdata
where
      categoryId=#{categoryId}
      AND YEAR(latestDate)=#{year}
      AND MONTH(latestDate)=#{month}
      AND DAY(latestDate)=#{day}
ORDER BY
        ${order} DESC
limit
      #{start},#{pageSize}

select
      count(*)
from
      wechatdata
where
      categoryId=#{categoryId}
      AND YEAR(latestDate)=#{year}
      AND MONTH(latestDate)=#{month}
      AND DAY(latestDate)=#{day}
